    var app = new Vue({
      el: '#app',
      data: {
        open: false,
        initializing: true,
        items: null,
        test: "Visualization",
        postData: null,
        search:{
          python: false,
          sql: false,
          title: null
        }
      },
      created() {
        axios
        .get( '../docs/posts.json').then(item => {
          this.initializing = false
          const values = Object.values(item.data)
          values.map(res => {
            if((typeof res.tech) === 'string') {
              res.tech = res.tech.split()
            } 
          })
          this.postData = values 
        })
      },
      computed: {
        filter() {
          let test = this.postData.filter(item => {    
            if (this.search.title) {
              if (item.title.includes(this.search.title)) return true
              return false
            }
            if (this.search.python) {
              if (item.tech.includes("Python")) return true
              return false
            }
            if (this.search.sql) {
              if (item.tech.includes("Sql")) return true
              return false
            }
            return true
          })
          return test
        }
      },
      methods: {
        showR(item) {
          item.map(res=> {
            if(item.find(i => i == 'R')) return true
            else return false
          })
        },
        showPython(item) {
          if(item.find(i => i == 'Python')) return true
          else return false
        },
        showSql(item) {
          if(item.find(i => i == 'Sql')) return true
          else return false
        },
        postUrl(post) {
          return './posts' + post.date + '-' + (post.title.toLocaleLowerCase()).replace(/ /g,"-")
        }
      }
    })
  


